import React, {useEffect, useState} from 'react';
import clsx from 'clsx';
import {fade, makeStyles} from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import CssBaseline from '@material-ui/core/CssBaseline';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import GamesIcon from '@material-ui/icons/Games';
import FormatIndentDecreaseIcon from '@material-ui/icons/FormatIndentDecrease';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import SearchIcon from '@material-ui/icons/Search';
import InputBase from '@material-ui/core/InputBase';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import {useDispatch, useSelector} from 'react-redux';
import {getProfile, Logout} from '../../Slice/useSlice';
import {Switch, Route, useHistory} from "react-router-dom";
import {Avatar, Badge, Button, ListSubheader, Typography} from '@material-ui/core';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import Login from '../Login';
import Register from '../Register';
import Profile from './Profile/Profile';
import FooterLayout from '../Footer'
import ProductList from './ProductList/ProductList';
import NavLinkAdapter from '../NavLinkAdapter';
import DetailPage from './DetailPage/DetailPage';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import { cartItemCount } from '../../Slice/selecters';
import { unwrapResult } from '@reduxjs/toolkit';
import ShopCart from './ShopCart/ShopCart';
import Order from './Order/Order';
import Error404page from '../404/Error404page'

const drawerWidth = 240;

const useStyles = makeStyles(theme => ({
    root: {
        display: 'flex',
    },
    appBar: {
        transition: theme.transitions.create(['margin', 'width'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
        }),
        backgroundColor: '#252f3e',
        color: '#fff'
    },
    appBarShift: {
        width: `calc(100% - ${drawerWidth}px)`,
        marginLeft: drawerWidth,
        transition: theme.transitions.create(['margin', 'width'], {
            easing: theme.transitions.easing.easeOut,
            duration: theme.transitions.duration.enteringScreen,
        }),
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
    hide: {
        display: 'none',
    },
    drawer: {
        width: drawerWidth,
        flexShrink: 0,
    },
    drawerPaper: {
        width: drawerWidth,
    },
    drawerHeader: {
        display: 'flex',
        alignItems: 'center',
        padding: '0 8px',
        ...theme.mixins.toolbar,
        justifyContent: 'flex-end',
    },
    content: {
        padding: theme.spacing(2),
        flexGrow: 1,
        paddingBottom: theme.spacing(4),
        backgroundColor: theme.palette.background.paper,
        transition: theme.transitions.create('margin', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
        }),
        marginLeft: -drawerWidth
    },
    contentShift: {
        transition: theme.transitions.create('margin', {
            easing: theme.transitions.easing.easeOut,
            duration: theme.transitions.duration.enteringScreen,
        }),
        marginLeft: 0,
    },
    logo: {
        fontSize: '1.8571428571428572rem',
        fontFamily: 'Poppins,Roboto,"Helvetica",Arial,sans-serif',
        fontWeight: 500,
        lineHeight: 1.6,
        color: '#fff'
    },
    text: {
        fontSize: '1.5rem',
