                <div className={classes.drawerHeader}/>
                <Switch>
                    <Route path='/product' exact>
                        <ProductList/>
                    </Route>
                    <Route path='/' exact>
                        <ProductList/>
                    </Route>
                    <Route path="/login">
                        <Login/>
                    </Route>
                    <Route path="/register">
                        <Register/>
                    </Route>
                    <Route path="/profile">
                        <Profile/>
                    </Route>
